export default async function handler(e,t){if("POST"!==e.method)return t.status(405).json({message:"Method not allowed"});const n=process.env.CGM_OAUTH_URL;return n?t.status(200).json({url:n}):t.status(200).json({message:"CGM connect is not configured yet. Set CGM_OAUTH_URL in env."})}